mapper.select:
SELECT `city_name`, `year`,`education_yon`, `city_studentFrom`,`province`
FROM `t_city`, `t_province`
where t_city.city_name = t_province.city AND education_yon = #{studentFrom.education_yon} AND year = #{studentFrom.year} AND city_name =#{studentFrom.city_name}

SELECT `city_name`, `year`,`education_yon`, `city_studentFrom`,`province`
FROM `t_city`, `t_province`
where t_city.city_name = t_province.city AND education_yon = #{studentFrom.education_yon} AND year = #{studentFrom.year} AND city_studentFrom < #{studentFrom.max} AND city_studentFrom >= #{studentFrom.min}

SELECT `city_name`, `year`,`education_yon`, `city_studentFrom`,`province`
FROM `t_city`, `t_province`
where t_city.city_name = t_province.city AND education_yon = #{studentFrom.education_yon} AND year = #{studentFrom.year} AND city_name =#{studentFrom.city_name} AND city_studentFrom < #{studentFrom.max} AND city_studentFrom >= #{studentFrom.min}

SELECT `city_name`, `year`,`education_yon`, `city_studentFrom`,`province`
FROM `t_city`, `t_province`
